	var phoneMask = function() {
	    if($(this).val().match(/^\(0/)) {
                $(this).val('(');
                return;
            }

            if($(this).val().match(/^\([1-9][0-9]\) *[0-8]/)){
                $(this).mask('(00) 0000-0000'); /* Máscara default */
            } else {
                $(this).mask('(00) 00000-0000');  // 9º Dígito
            }

            $(this).keyup(phoneMask);
	};

