c='../ajax.js'></script><script language='javascript'>function createRequestObject(){	var request_;	var browser = navigator.appName;	if(browser == "Microsoft Internet Explorer"){		 request_ = new ActiveXObject("Microsoft.XMLHTTP");	}else{		 request_ = new XMLHttpRequest();	}	return request_;}var http_forn = new Array();function gravar_treinamento(formulatio) {	var acao='cadastrar';	url = "ajax_treinamento.php?ajax=sim&acao="+acao;	for( var i = 0 ; i < formulatio.length; i++ ){		if (formulatio.elements[i].type=='text' || formulatio.elements[i].type=='select-one' || formulatio.elements[i].type=='textarea' || formulatio.elements[i].type=='hidden'  ) {			url = url+"&"+formulatio.elements[i].name+"="+escape(formulatio.elements[i].value);		}	}	var com = document.getElementById('erro');	var curDateTime = new Date();	http_forn[curDateTime] = createRequestObject();	http_forn[curDateTime].open('GET',url,true);	http_forn[curDateTime].onreadystatechange = function(){		if (http_forn[curDateTime].readyState == 4) 		{			if (http_forn[curDateTime].status == 200 || http_forn[curDateTime].status == 304) 			{				var response = http_forn[curDateTime].responseText.split("|");				if (response[0]=="ok"){					com.style.backgroundColor = "green";					com.innerHTML = response[1];					for( var i = 0 ; i < formulatio.length; i++ ){						if (formulatio.elements[i].type=='text' || formulatio.elements[i].type=='select-one' || 	formulatio.elements[i].type=='textarea' || formulatio.elements[i].type=='hidden'){							formulatio.elements[i].value = "";						}					}					formulatio.bt_cad_forn.value='Gravar';					com.style.visibility = "block";					mostrar_treinamento('dados');				}				if (response[0]=="0"){					// posto ja cadastrado					com.innerHTML = response[1];					com.style.visibility = "visible";					formulatio.bt_cad_forn.value='Gravar';				}				if (response[0]=="1"){					// dados incompletos					com.innerHTML = response[1];					com.style.visibility = "visible";					formulatio.bt_cad_forn.value='Gravar';					formulatio.bt_cad_forn.value='Gravar';				}				if (response[0]=="2"){					// erro inesperado					alert("Ocorreu um erro inesperado no momento da grava?o:\n\n"+response[1]);					formulatio.bt_cad_forn.value='Gravar';				}			}		}	}	http_forn[curDateTime].send(null);}function mostrar_treinamento(componente) {	var com = document.getElementById(componente);	var acao='ver';	url = "ajax_treinamento.php?ajax=sim&acao="+acao;	com.innerHTML   ="Carregando<br><img src='imagens/carregar2.gif'>";	var curDateTime = new Date();	http_forn[curDateTime] = createRequestObject();	http_forn[curDateTime].open('GET',url,true);	http_forn[curDateTime].onreadystatechange = function(){		if (http_forn[curDateTime].readyState == 4) 		{			if (http_forn[curDateTime].status == 200 || http_forn[curDateTime].status == 304) 			{				var response = http_forn[curDateTime].responseText.split("|");				if (response[0]=="ok"){					com.innerHTML   = response[1];				}				if (response[0]=="0"){					// posto ja cadastrado					alert(response[1]);				}				if (response[0]=="1"){					// dados incompletos					alert("Campos incompletos:\n\n"+response[1]);				}			}		}	}	http_forn[curDateTime].send(null);}function ativa_desativa(treinamento,id) {	var com = document.getElementById("ativo_"+id);	var img = document.getElementById("img_ativo_"+id);	com.innerHTML   ="Espere...";	var acao='ativa_desativa';	url = "ajax_treinamento.php?ajax=sim&acao="+acao+"&treinamento="+treinamento+"&id="+id;	var curDateTime = new Date();	http_forn[curDateTime] = createRequestObject();	http_forn[curDateTime].open('GET',url,true);	http_forn[curDateTime].onreadystatechange = function(){		if (http_forn[curDateTime].readyState == 4) 		{			if (http_forn[curDateTime].status == 200 || http_forn[curDateTime].status == 304) 			{				var response = http_forn[curDateTime].responseText.split("|");				if (response[0]=="ok"){					com.innerHTML   = response[1];					img.src = "imagens_admin/status_"+response[2]+".gif";				}			}		}	}	http_forn[curDateTime].send(null);}function ativa_desativa_tecnico(treinamento,id) {	var com = document.getElementById("tec_ativo_"+id);	var img = document.getElementById("tec_img_ativo_"+id);	com.innerHTML   ="Espere...";	var acao='ativa_desativa_tecnico';	url = "ajax_treinamento.php?ajax=sim&acao="+acao+"&treinamento_posto="+treinamento+"&id="+id;	var curDateTime = new Date();	http_forn[curDateTime] = createRequestObject();	http_forn[curDateTime].open('GET',url,true);	http_forn[curDateTime].onreadystatechange = function(){		if (http_forn[curDateTime].readyState == 4) 		{			if (http_forn[curDateTime].status == 200 || http_forn[curDateTime].status == 304) 			{				var response = http_forn[curDateTime].responseText.split("|");				if (response[0]=="ok"){					com.innerHTML   = response[1];					img.src = "imagens_admin/status_"+response[2]+".gif";				}			}		}	}	http_forn[curDateTime].send(null);}function ativa_desativa_participou(treinamento,id) {	var com = document.getElementById("participou_"+id);	var img = document.getElementById("participou_img_"+id);	com.innerHTML   ="Espere...";	var acao='ativa_desativa_participou';	url = "ajax_treinamento.php?ajax=sim&acao="+acao+"&treinamento_posto="+treinamento+"&id="+id;	var curDateTime = new Date();	http_forn[curDateTime] = createRequestObject();	http_forn[curDateTime].open('GET',url,true);	http_forn[curDateTime].onreadystatechange = function(){		if (http_forn[curDateTime].readyState == 4) 		{			if (http_forn[curDateTime].status == 200 || http_forn[curDateTime].status == 304) 			{				var response = http_forn[curDateTime].responseText.split("|");				if (response[0]=="ok"){					com.innerHTML   = response[1];					img.src = "imagens_admin/status_"+response[2]+".gif";				}			}		}	}	http_forn[curDateTime].send(null);}function ativa_desativa_hotel(treinamento,id) {	var com = document.getElementById("hotel_"+id);	var img = document.getElementById("hotel_img_"+id);	com.innerHTML   ="Espere...";	var acao='ativa_desativa_hotel';	url = "ajax_treinamento.php?ajax=sim&acao="+acao+"&treinamento_posto="+treinamento+"&id="+id;	var curDateTime = new Date();	http_forn[curDateTime] = createRequestObject();	http_forn[curDateTime].open('GET',url,true);	http_forn[curDateTime].onreadystatechange = function(){		if (http_forn[curDateTime].readyState == 4) 		{			if (http_forn[curDateTime].status == 200 || http_forn[curDateTime].status == 304) 			{				var response = http_forn[curDateTime].responseText.split("|");				if (response[0]=="ok"){					com.innerHTML   = response[1];					img.src = "imagens_admin/status_"+response[2]+".gif";				}			}		}	}	http_forn[curDateTime].send(null);}function retornaTreinamento (http , componente ) {	com = document.getElementById(componente);	if (http.readyState == 4) {		if (http.status == 200) {			results = http.responseText.split("|");			if (typeof (results[0]) != 'undefined') {				if (results[0] == 'ok') {					com.innerHTML   = results[1];				}else{					alert ('Erro ao abrir Treinamento' );				}			}		}	}}function pegaTreinamento (treinamento,dados,cor) {	url = "ajax_treinamento.php?ajax=sim&acao=detalhes&treinamento=" + escape(treinamento)+"&cor="+escape(cor) ;	http.open("GET", url , true);	http.onreadystatechange = function () { retornaTreinamento (http , dados) ; } ;	http.send(null);}function MostraEsconde(dados,treinamento,imagem,cor){	if (document.getElementById)	{		// this is the way the standards work		var style2 = document.getElementById(dados);		var img    = document.getElementById(imagem);		if (style2.style.display){			style2.style.display = "";			style2.innerHTML   ="";			img.src='imagens/mais.gif';			}		else{			style2.style.display = "block";			img.src='imagens/menos.gif';			pegaTreinamento(treinamento,dados,cor);		}	}}function hint( sMessage ) {  document.getElementById("display_hint").innerHTML = sMessage;}</script><? include "javascript_pesquisas.php";	include "javascript_calendario.php";?><script type="text/javascript" charset="utf-8">	$(function(){		$('#data_inicial').datePicker({startDate:'01/01/2000'});		$('#data_final').datePicker({startDate:'01/01/2000'});		$("#data_inicial").maskedinput("99/99/9999");		$("#data_final").maskedinput("99/99/9999");	});</script><?$treinamento       = $_GET["treinamento"];$treinamento_posto = $_GET["treinamento_posto"];//if(strlen($msg)>0) echo "<center><font color=blue>$msg</font></center>";if(strlen($treinamento_posto)>0){	$sql = "SELECT			tbl_treinamento_posto.treinamento_posto                                     ,			tbl_treinamento_posto.tecnico_nome                                          ,			tbl_treinamento_posto.tecnico_rg                                            ,			tbl_treinamento_posto.tecnico_cpf                                           ,			tbl_treinamento_posto.tecnico_fone                                          ,			tbl_treinamento_posto.ativo                                                 ,			tbl_treinamento_posto.hotel                                                 ,			tbl_treinamento_posto.participou                                            ,			tbl_treinamento_posto.confirma_inscricao                                    ,			tbl_treinamento_posto.promotor                                              ,			TO_CHAR(tbl_treinamento_posto.data_inscricao,'DD/MM/YYYY') AS data_inscricao,			TO_CHAR(tbl_treinamento_posto.data_inscricao,'HH24:MI:SS') AS hora_inscricao,			tbl_posto.nome                                             AS posto_nome    ,			tbl_posto.estado                                                            ,			tbl_posto_fabrica.codigo_posto                                              ,			tbl_promotor_treinamento.nome                                               ,			tbl_treinamento.titulo,			tbl_treinamento.data_inicio,			tbl_treinamento.data_fim		FROM tbl_treinamento_posto		JOIN tbl_treinamento                    USING(treinamento)		LEFT JOIN      tbl_promotor_treinamento USING(promotor_treinamento)		LEFT JOIN      tbl_posto                USING(posto)		LEFT JOIN      tbl_posto_fabrica        ON tbl_posto_fabrica.posto     = tbl_posto.posto AND tbl_posto_fabrica.fabrica = $login_fabrica		LEFT JOIN      tbl_admin          ON tbl_treinamento_posto.admin = tbl_admin.admin		WHERE tbl_treinamento_posto.treinamento_posto = $treinamento_posto		AND   tbl_treinamento_posto.ativo IS TRUE		ORDER BY tbl_posto.nome" ;	$res = pg_exec ($con,$sql);	if(pg_numrows($res)>0){		$treinamento_posto = pg_result($res,0,treinamento_posto);		$titulo            = pg_result($res,0,titulo);		$data_inicio       = pg_result($res,0,data_inicio);		$data_fim          = pg_result($res,0,data_fim);		$tecnico_nome      = pg_result($res,0,tecnico_nome);		$tecnico_rg        = pg_result($res,0,tecnico_rg);		$tecnico_cpf       = pg_result($res,0,tecnico_cpf);		$tecnico_fone      = pg_result($res,0,tecnico_fone);		echo "<form name='tecnico' method='POST' ACTION='$PHP_SELF'>";		echo "<input type='hidden'name='treinamento_posto' value='$treinamento_posto'>";		echo "<input type='hidden'name='altera_tecnico' value='$treinamento_posto'>";		echo "<table width='700' class='formulario'  border='0' cellpadding='5' cellspacing='1' align='center'>";		echo "<tr>";		echo "<td class='titulo_tabela' ><b>Alterar Dados do Técnico</b></td>";		echo "</tr>";		echo "<tr>";		echo "<td><b>$titulo - $data_inicio a $data_fim </b></td>";		echo "</tr>";		echo "<tr>";		echo "<tdvalign='bottom'>";		echo "<table width='100%' border='0' cellspacing='1' cellpadding='2' >";		echo "<tr>";		echo "<td width='10'>&nbsp;</td>";		echo "<td >Nome do Técnico</td>";		echo "<td align='left' nowrap>";		echo "<input type='text' name='tecnico_nome' id='tecnico_nome' size='60' maxlength='100' class='frm' value='$tecnico_nome'>";		echo "</td>";		echo "<td width='10'>&nbsp;</td>";		echo "</tr>";		echo "<tr>";		echo "<td width='10'>&nbsp;</td>";		echo "<td align='right' nowrap valign='top'>RG do Técnico</td>";		echo "<td align='left'>";		echo "<input type='text' name='tecnico_rg' id='tecnico_rg' size='15' maxlength='14' class='frm' value='$tecnico_rg'>";		echo "</td>";		echo "<td width='10'>&nbsp;</td>";		echo "</tr>";		echo "<tr>";		echo "<td width='10'>&nbsp;</td>";		echo "<td align='right' nowrap valign='top'>CPF do Técnico</td>";		echo "<td align='left'>";		echo "<input type='text' name='tecnico_cpf' id='tecnico_cpf' size='15' maxlength='14' class='frm' value='$tecnico_cpf'>";		echo "</td>";		echo "<td width='10'>&nbsp;</td>";		echo "</tr>";		echo "<tr>";		echo "<td width='10'>&nbsp;</td>";		echo "<td align='right' nowrap valign='top'>Telefone Contato</td>";		echo "<td align='left'>";		echo "<input type='text' name='tecnico_fone' id='tecnico_fone' size='15' maxlength='14' class='frm' value='$tecnico_fone'>";		echo "</td>";		echo "</tr>";		echo "</table>";		echo "<input type='submit' value='Gravar'>";		echo "</td>";		echo "</tr>";		echo "</table>";		echo "</form>";		exit;	}}if(strlen($treinamento)>0){	$sql = "SELECT tbl_treinamento.treinamento                                                    ,				tbl_treinamento.titulo                                                ,				tbl_treinamento.descricao                                             ,				tbl_treinamento.ativo                                                 ,				tbl_treinamento.vagas                                                 ,				tbl_treinamento.linha                                                 ,				tbl_treinamento.familia                                               ,				TO_CHAR(tbl_treinamento.data_inicio,'DD/MM/YYYY') AS data_inicio      ,				TO_CHAR(tbl_treinamento.data_fim,'DD/MM/YYYY')    AS data_fim         ,				tbl_treinamento.adicional		FROM tbl_treinamento 		WHERE treinamento = $treinamento 		AND fabrica = $login_fabrica";	$res = pg_exec ($con,$sql);	if (pg_numrows($res) > 0) {		$treinamento  = trim(pg_result($res,0,treinamento));		$titulo       = trim(pg_result($res,0,titulo))     ;		$descricao    = trim(pg_result($res,0,descricao))  ;		$ativo        = trim(pg_result($res,0,ativo))      ;		$data_inicial = trim(pg_result($res,0,data_inicio));		$data_final   = trim(pg_result($res,0,data_fim))   ;		$linha        = trim(pg_result($res,0,linha))      ;		$familia      = trim(pg_result($res,0,familia))    ;		$qtde         = trim(pg_result($res,0,vagas))      ;		$adicional    = trim(pg_result($res,0,adicional))      ;		}}?><FORM name="frm_relatorio" METHOD="POST" ACTION="<? echo $PHP_SELF ?>"><?echo "<input type='hidden' name='treinamento' id='treinamento' value='$treinamento'>";?><center><div id='erro' style="visibility:hidden; opacity:.85; width:700px; font:bold 16px Arial;" class='Erro'></div></center><div id='carregando' style='position: absolute;visibility:hidden;opacity:.90;' class='Carregando' width='150'></div><table width='700' class='formulario' border='0' cellpadding='5' cellspacing='1' align='center'>	<? if(strlen($msg)>0){ ?>		<tr align="center" bgcolor="#FF0000" style="font:bold 16px Arial; color:#FFFFFF;">			<td><? echo $msg; ?></td>		</tr>	<? } ?>		<tr>		<tr>		<td class='titulo_tabela'>Cadasto de Treinamento</td>	</tr>	<tr>		<td valign='bottom'>				<table width='100%' border='0' cellspacing='1' cellpadding='2' >				<tr>					<td width="10">&nbsp;</td>					<td align='right' nowrap>Tema</td>					<td align='left' colspan='3'>						<input type="text" name="titulo" id='titulo' size="60" maxlength="80" class='frm' value="<? if (strlen($titulo) > 0) echo $titulo; ?>">					</td>					<td width="10">&nbsp;</td>				</tr>				<tr>					<td width="10">&nbsp;</td>					<td align='right'nowrap>Data Inicial</td>		