_familia = trim(pg_result($res,$x,familia));							$aux_nome  = trim(pg_result($res,$x,descricao));														echo "<option value='$aux_familia'"; 							if ($familia == $aux_familia){								echo " SELECTED "; 								$mostraMsgLinha = "<br> da FAMILIA $aux_nome";							}							echo ">$aux_nome</option>\n";						}						echo "</select>\n";					}					?>					</td>					<td width="10">&nbsp;</td>				</tr>				<tr>					<td width="10">&nbsp;</td>					<td align='right' nowrap valign='top'>Vagas</td>					<td align='left'>						<input type="text" name="qtde" size="10" maxlength="2" class='frm' value="<? if (strlen($qtde) > 0) echo $qtde; ?>">					</td>					<td width="10">&nbsp;</td>				</tr>				<tr>					<td width="10">&nbsp;</td>					<td align='right' nowrap valign='top'>Informações Adicionais</td>					<td align='left' colspan='3'>											<input type="text" name="adicional" size="60" maxlength="200" class='frm' value="<? echo $adicional; ?>">					<DIV ID="display_hint">Digite aqui as informações adicionais que o posto deve <br>fornecer ao inscrever um treinando. Ex: Revenda </DIV>					</td>					<td width="10">&nbsp;</td>				</tr>				<tr>					<td colspan="4">&nbsp;</td>				</tr>				<tr>					<td width="10">&nbsp;</td>					<td align='right' nowrap valign='top'>Descrição</td>					<td align='left' colspan='3'>					<TEXTAREA NAME='descricao' ROWS='7' COLS='60' class='frm'><?if (strlen($descricao) > 0) echo $descricao; ?></TEXTAREA>					</td>					<td width="10">&nbsp;</td>				</tr>			</table><br>			<INPUT TYPE='button' name='bt_cad_forn' id='bt_cad_forn' value='Gravar' onClick="if (this.value=='Gravando...'){ alert('Aguarde');}else {this.value='Gravando...'; gravar_treinamento(this.form);}">		</td>	</tr></table></FORM><br /><?echo "<div id='dados'></div>";echo "<script language='javascript'>mostrar_treinamento('dados');</script>";?><p><? include "rodape.php" ?>
