DateTime] = createRequestObject();	http_forn[curDateTime].open('GET',url,true);	http_forn[curDateTime].onreadystatechange = function(){		if (http_forn[curDateTime].readyState == 4) 		{			if (http_forn[curDateTime].status == 200 || http_forn[curDateTime].status == 304) 			{				var response = http_forn[curDateTime].responseText.split("|");				if (response[0]=="ok"){					com.innerHTML   = response[1];				}				if (response[0]=="0"){					alert(response[1]);				}			}		}	}	http_forn[curDateTime].send(null);}</script><? include "javascript_pesquisas.php" ?><?$promotor_treinamento = $_GET["promotor_treinamento"];if(strlen($promotor_treinamento)>0){	$sql = "SELECT tbl_promotor_treinamento.*		FROM tbl_promotor_treinamento		WHERE promotor_treinamento = $promotor_treinamento		AND tbl_promotor_treinamento.fabrica = $login_fabrica";	$res = pg_exec ($con,$sql);	if (pg_numrows($res) > 0) {		$promotor     = trim(pg_result($res,0,promotor_treinamento));		$nome         = trim(pg_result($res,0,nome));		$email        = trim(pg_result($res,0,email));		$ativo        = trim(pg_result($res,0,ativo));		$tipo_posto   = trim(pg_result($res,0,tipo_posto));		$regiao       = trim(pg_result($res,0,regiao));	}}if(strlen($msg)>0)	echo "<font color='#009900'>$msg</font>";if(strlen($msg_erro)>0){	echo "<font color='#990000'>".$msg_erro."</font>";}?><FORM name="frm_relatorio" METHOD="POST" ACTION="<? echo $PHP_SELF ?>"><?echo "<input type='hidden' name='promotor' id='promotor' value='$promotor'>";?><div id='erro' style='visibility:hidden;opacity:.85;' class='Erro'></div><div id='carregando' style='position: absolute;visibility:hidden;opacity:.90;' class='Carregando' width='150'></div><table width='500' class='Conteudo' style='background-color: #485989' border='0' cellpadding='5' cellspacing='1' align='center'>		<tr>		<td class='Titulo' background='imagens_admin/azul.gif'><b>PROMOTOR</b></td>	</tr>		<tr>		<td bgcolor='#DBE5F5' valign='bott