ument.getElementById("ativo_"+id);	var img = document.getElementById("img_ativo_"+id);	com.innerHTML   ="Espere...";	var acao='ativa_desativa';	url = "ajax_treinamento.php?ajax=sim&acao="+acao+"&treinamento="+treinamento+"&id="+id;	var curDateTime = new Date();	http_forn[curDateTime] = createRequestObject();	http_forn[curDateTime].open('GET',url,true);	http_forn[curDateTime].onreadystatechange = function(){		if (http_forn[curDateTime].readyState == 4) 		{			if (http_forn[curDateTime].status == 200 || http_forn[curDateTime].status == 304) 			{				var response = http_forn[curDateTime].responseText.split("|");				if (response[0]=="ok"){					com.innerHTML   = response[1];					img.src = "imagens_admin/status_"+response[2]+".gif";				}			}		}	}	http_forn[curDateTime].send(null);}function ativa_desativa_tecnico(treinamento,id) {	var com = document.getElementById("tec_ativo_"+id);	var img = document.getElementById("tec_img_ativo_"+id);	com.innerHTML   ="Espere...";	var acao='ativa_desativa_tecnico';	url = "ajax_treinamento.php?ajax=sim&acao="+acao+"&treinamento_posto="+treinamento+"&id="+id;	var curDateTime = new Date();	http_forn[curDateTime] = createRequestObject();	http_forn[curDateTime].open('GET',url,true);	http_forn[curDateTime].onreadystatechange = function(){		if (http_forn[curDateTime].readyState == 4) 		{			if (http_forn[curDateTime].status == 200 || http_forn[curDateTime].status == 304) 			{				var response = http_forn[curDateTime].responseText.split("|");				if (response[0]=="ok"){					com.innerHTML   = response[1];					img.src = "imagens_admin/status_"+response[2]+".gif";				}			}		}	}	http_forn[curDateTime].send(null);}function ativa_desativa_participou(treinamento,id) {	var com = document.getElementById("participou_"+id);	var img = document.getElementById("participou_img_"+id);	com.innerHTML   ="Espere...";	var acao='ativa_desativa_participou';	url = "ajax_treinamento.php?ajax=sim&acao="+acao+"&treinamento_posto="+treinamento+"&id="+id;	var curDateTime = new Date();	http_forn[curDateTime] = createRequestObject();	http_forn[curDateTime].open('GET',url,true);	http_forn[curDateTime].onreadystatechange = function(){		if (http_forn[curDateTime].readyState == 4) 		{			if (http_forn[curDateTime].status == 200 || http_forn[curDateTime].status == 304) 			{				var response = http_forn[curDateTime].responseText.split("|");				if (response[0]=="ok"){					com.innerHTML   = response[1];					img.src = "imagens_admin/status_"+response[2]+".gif";				}			}		}	}	http_forn[curDateTime].send(null);}function retornaTreinamento (http , componente ) {	com = document.getElementById(componente);	//com.innerHTML   ="Carregando<br><img src='imagens/carregar2.gif'>"; hd 268395	if (http.readyState == 4) {		if (http.status == 200) {			results = http.responseText.split("|");			if (typeof (results[0]) != 'undefined') {				if (results[0] == 'ok') {					com.innerHTML   = results[1];				}else{					alert ('Erro ao abrir Treinamento' );				}			}		}	}}function pegaTreinamento (treinamento,dados,cor) {	url = "ajax_treinamento.php?ajax=sim&acao=detalhes&treinamento=" + escape(treinamento)+"&cor="+escape(cor) ;	http.open("GET", url , true);	http.onreadystatechange = function () { retornaTreinamento (http , dados) ; } ;	http.send(null);}function MostraEsconde(dados,treinamento,imagem,cor){	if (document.getElementById)	{		// this is the way the standards work		var style2 = document.getElementById(dados);		var img    = document.getElementById(imagem);		if (style2.style.display){			style2.style.display = "";			style2.innerHTML   ="";			img.src='imagens/mais.gif';			}		else{			style2.style.display = "block";			img.src='imagens/menos.gif';			pegaTreinamento(treinamento,dados,cor);		}	}}</script><? //include "javascript_pesquisas.php" ?><?//echo "<h1>Treinamentos agendados</h1>";echo "<div id='dados'></div>";echo "<script language='javascript'>mostrar_treinamento('dados');</script>";?><p><? include "rodape.php" ?>