  ,						produto_referencia,						produto_descricao ,						FA.familia        ,						familia_nome					ORDER BY familia_nome";			$res = pg_exec($con,$sql);			$total = pg_numrows($res);			echo "<input type='hidden' name='qtde_item' id='qtde_item' value='$total'>";			if(pg_numrows($res)>0){				echo "\n\n<ul id='tree' style='font-size:10px;'>\n";				for($i=0;$i<pg_numrows($res);$i++) {					$familia              = pg_result($res,$i,familia);					$familia_nome         = pg_result($res,$i,familia_nome);					$produto              = pg_result($res,$i,produto);					$produto_referencia   = pg_result($res,$i,produto_referencia);					$produto_descricao    = pg_result($res,$i,produto_descricao);					$total                = pg_result($res,$i,total);					if($familia_nome<>$xfamilia_nome){						if($i<>0){							echo "				</ul>\n";							echo "			</li>\n";						}						echo "			<li><strong><a onclick='marcar($familia)'>$familia_nome</a></strong>\n";						echo "				<ul>\n";					}					echo "					<li><input type='checkbox' name='produto_$i' id='produto_$i' value='$produto' rel='$familia'>$produto_referencia - $produto_descricao - $fornecedor_descricao (Total: $total)</li>\n";					//checked 					$xfamilia_nome = $familia_nome;				}				if($familia_nome==$xfamilia_nome){					echo "				</ul>\n";					echo "			</li>\n";				}				echo "</u1><br>";				echo "<input type='button' name='btn_acao'  value='Gravar' onClick=\"if (this.value!='Gravar'){ alert('Aguarde');}else {this.value='Gravando...'; gravar(this.form,'sim','$PHP_SELF','nao');}\" style=\"width: 150px;\"></td>";				echo "<div id='saida' style='display:inline;'></div>";			}else echo "<center>Nenhum produto disponível para reparo/conserto</center>";		?>		<div id='erro' style='visibility:hidden;opacity:0.85' class='Erro'></div>		</form>		</div>	</div></div><?include "rodape.php";?>