inha           LI USING(linha)					JOIN    tbl_familia         FA USING(familia)					JOIN    tbl_login_unico     LU ON LU.login_unico = RI.tecnico					WHERE RI.posto = $login_posto					AND   RI.data_devolucao IS NULL					$sql_add 					ORDER BY produto_descricao";			$res = pg_exec($con,$sql);			$total = pg_numrows($res);			echo "<input type='hidden' name='qtde_item' id='qtde_item' value='$total'>";			if(pg_numrows($res)>0){				echo "<table style=' background-color: #e6eef7;font-size:12px;border-collapse: collapse' align='center'  border='1' cellpadding='5' cellspacing='0' bordercolor='#000000' width='98%'>\n";				echo "<caption><a href='planilha_print.php?tecnico=$tecnico' target='_blank'><img src='imagens/icone_imprimir.png' title='Imprimir Planilha'></a></caption>";				echo "<thead>\n";				echo "<tr bgcolor='#596D9B' style='color:#FFFFFF;' ALIGN='CENTER'>\n";				echo "<td ><b>RG</b></td>\n";				echo "<td ><b>PRODUTO</b></td>\n";				echo "<td ><b>SÉRIE</b></td>\n";				echo "<td ><b>DEFEITO RECLAMADO</b></td>\n";				echo "<td ><b>DEFEITO CONSTATADO</b></td>\n";				echo "<td ><b>DATA</b></td>\n";				echo "<td ><b></b></td>\n";				echo "</tr>\n";				echo "</thead>\n";				echo "<tbody>\n";				for($i=0;$i<pg_numrows($res);$i++) {					$produto_rg_item = pg_result($res,$i,produto_rg_item);					$rg              = pg_result($res,$i,rg);					$codigo_barra    = pg_result($res,$i,codigo_barra);					$produto         = pg_result($res,$i,produto);					$familia_nome    = pg_result($res,$i,familia_nome);					$linha_nome      = pg_result($res,$i,linha_nome);					$produto_nome    = pg_result($res,$i,produto_descricao);					$data            = pg_result($res,$i,data);					$x = $i+1;					if($cor<>'#FFFFFF') $cor = '#FFFFFF';					else                $cor = '#e6eef7';					echo "<tr bgcolor='$cor' >\n";					echo "<td>&nbsp;&nbsp;$rg</td>\n";					echo "<td>$produto_nome</td>\n";					echo "<td>$serie&nbsp;</td>\n";					echo "<td>$defeito_reclamado&